using namespace std;

// 快速幂函数：计算 a^b
int qp(int a, int b) {
    int r = 1;         // r 存储结果，初始为 1
    while (b &gt; 0) {    // 当指数 b 大于 0 时继续循环
        if (b % 2)     // 如果 b 是奇数
            r *= a;    // 将当前基数累乘到结果
        a *= a;        // 基数自乘
        b /= 2;        // 指数减半
    }
    return r;          // 返回计算结果
}

int main() {
    int a, b;
    cout &lt;&lt; "输入底数和指数：";
    cin &gt;&gt; a &gt;&gt; b;              // 输入底数 a 和指数 b
    cout &lt;&lt; a &lt;&lt; "^" &lt;&lt; b &lt;&lt; " = " &lt;&lt; qp(a, b) &lt;&lt; endl;
    return 0;
}



