using namespace std;

// 定义长整型别名，便于后续使用
typedef long long LL;

// 快速幂函数，计算 (a^k) % p
int qmi(int a, int k, int p)
{
    int res = 1; // 初始化结果为 1
    while (k) // 当指数 k 不为 0 时循环
    {
        if (k &amp; 1) // 如果 k 是奇数，累乘当前的 a 并取模
            res = (LL)res * a % p;
        k /= 2; // 指数 k 右移一位，相当于整除 2
        a = (LL)a * a % p; // 底数自乘并取模
    }
    return res; // 返回计算结果
}

int main()
{
    int n;
    cin &gt;&gt; n; // 读取测试用例数量
    while (n--) // 循环处理每个测试用例
    {
        int a, k, p;
        cin &gt;&gt; a &gt;&gt; k &gt;&gt; p; // 读取底数 a，指数 k 和模数 p
        cout &lt;&lt; qmi(a, k, p) &lt;&lt; endl; // 输出快速幂结果
    }
    return 0;
}
