using namespace std;
const int N = 110;
typedef pair&lt;int, int&gt; PII;
int d1[N][N], d2[N][N];  // 分别存储从起点和终点出发的距离
bool st1[N][N], st2[N][N];  // 标记数组，分别表示从起点和终点出发的访问情况
int g[N][N];  // 图的表示
PII Pre1[N][N], Pre2[N][N];  // 记录路径

int n, m;

int dx[4] = {-1, 0, 1, 0};  // 方向数组
int dy[4] = {0, 1, 0, -1};

// 双向 BFS
int bidirectionalBFS() {
    int hh1 = 0, tt1 = -1, hh2 = 0, tt2 = -1;
    PII q1[N * N], q2[N * N];  // 队列分别表示从起点和终点出发的队列
    q1[++tt1] = {1, 1};  // 起点
    q2[++tt2] = {n, m};  // 终点
    st1[1][1] = true;  // 起点标记
    st2[n][m] = true;  // 终点标记
    d1[1][1] = 0;  // 起点距离为0
    d2[n][m] = 0;  // 终点距离为0

    while (hh1 &lt;= tt1 || hh2 &lt;= tt2) {
        // 从起点进行 BFS
        if (hh1 &lt;= tt1) {
            auto t = q1[hh1++];
            for (int i = 0; i &lt; 4; i++) {
                int x = t.first + dx[i], y = t.second + dy[i];
                if (x &gt;= 1 &amp;&amp; x &lt;= n &amp;&amp; y &gt;= 1 &amp;&amp; y &lt;= m &amp;&amp; g[x][y] == 0 &amp;&amp; !st1[x][y]) {
                    d1[x][y] = d1[t.first][t.second] + 1;
                    Pre1[x][y] = t;
                    q1[++tt1] = {x, y};
                    st1[x][y] = true;
                }
            }
        }

        // 从终点进行 BFS
        if (hh2 &lt;= tt2) {
            auto t = q2[hh2++];
            for (int i = 0; i &lt; 4; i++) {
                int x = t.first + dx[i], y = t.second + dy[i];
                if (x &gt;= 1 &amp;&amp; x &lt;= n &amp;&amp; y &gt;= 1 &amp;&amp; y &lt;= m &amp;&amp; g[x][y] == 0 &amp;&amp; !st2[x][y]) {
                    d2[x][y] = d2[t.first][t.second] + 1;
                    Pre2[x][y] = t;
                    q2[++tt2] = {x, y};
                    st2[x][y] = true;
                }
            }
        }

        // 检查是否相遇
        for (int i = 1; i &lt;= n; i++) {
            for (int j = 1; j &lt;= m; j++) {
                if (st1[i][j] &amp;&amp; st2[i][j]) {
                    return d1[i][j] + d2[i][j];  // 返回相遇点的最短路径长度
                }
            }
        }
    }

    return -1;  // 如果没有找到路径
}

int main() {
    scanf("%d%d", &amp;n, &amp;m);
    for (int i = 1; i &lt;= n; i++)
        for (int j = 1; j &lt;= m; j++)
            cin &gt;&gt; g[i][j];

    printf("%d\n", bidirectionalBFS());  // 输出结果
    return 0;
}
