C++<pre><code>#include&lt;bits/stdc++.h&gt;
using namespace std;
const int N=100010;  // 定义数组最大容量
int q[N];            // 静态分配数组，避免动态内存开销

/**
 * 快速排序函数（双指针法）
 * @param q[] 待排序数组
 * @param l 当前区间的左边界
 * @param r 当前区间的右边界
 * 时间复杂度: 平均O(nlogn)，最坏O(n²)
 * 空间复杂度: O(logn) 递归栈空间
 */
void quick_sort(int q[], int l, int r) {
    // 递归终止条件：区间长度&lt;=1时无需排序
    if (l &gt;= r) return;

    // 选取中间元素作为基准值（比经典选首元素更抗退化）
    int x = q[l + r &gt;&gt; 1];  // 位运算等效 (l+r)/2
    int i = l - 1;          // 左扫描指针（从界外开始）
    int j = r + 1;          // 右扫描指针（从界外开始）

    // 核心分区逻辑：将数组分为&lt;=x和&gt;=x的两部分
    while (i &lt; j) {
        // 找到左边第一个 &gt;=x 的元素
        do i++; while (q[i] &lt; x);  // 注意没有等号，保证稳定性

        // 找到右边第一个 &lt;=x 的元素
        do j--; while (q[j] &gt; x);  // 注意没有等号，保证稳定性

        // 当指针未交叉时交换元素
        if (i &lt; j) swap(q[i], q[j]);
    }

    // 递归处理子区间（选择j作为分界点保证区间分裂）
    quick_sort(q, l, j);      // 处理左半区间
    quick_sort(q, j + 1, r);  // 处理右半区间
}

int main() {
    int n;
    cin &gt;&gt; n;

    // 输入数据
    for (int i = 0; i &lt; n; i++) 
        cin &gt;&gt; q[i];

    // 调用快速排序
    quick_sort(q, 0, n - 1);

    // 输出排序结果
    for (int i = 0; i &lt; n; i++) 
        cout &lt;&lt; q[i] &lt;&lt; " ";

    return 0;
}
