/*

001~abc-1, 999

abc
    1. num[i] &lt; x, 0
    2. num[i] == x, 0~efg
    3. num[i] &gt; x, 0~999

*/

int get(vector&lt;int&gt; num, int l, int r)
{
    int res = 0;
    for (int i = l; i &gt;= r; i -- ) res = res * 10 + num[i];
    return res;
}

int power10(int x)
{
    int res = 1;
    while (x -- ) res *= 10;
    return res;
}

int count(int n, int x)
{
    if (!n) return 0;

    vector&lt;int&gt; num;
    while (n)
    {
        num.push_back(n % 10);
        n /= 10;
    }
    n = num.size();

    int res = 0;
    for (int i = n - 1 - !x; i &gt;= 0; i -- )
    {
        if (i &lt; n - 1)
        {
            res += get(num, n - 1, i + 1) * power10(i);
            if (!x) res -= power10(i);
        }

        if (num[i] == x) res += get(num, i - 1, 0) + 1;
        else if (num[i] &gt; x) res += power10(i);
    }

    return res;
}

int main()
{
    int a, b;
    while (cin &gt;&gt; a &gt;&gt; b , a)
    {
        if (a &gt; b) swap(a, b);

        for (int i = 0; i &lt;= 9; i ++ )
            cout &lt;&lt; count(b, i) - count(a - 1, i) &lt;&lt; ' ';
        cout &lt;&lt; endl;
    }

    return 0;
}
