C++<pre><code>#include &lt;iostream&gt;
using namespace std;

int main() {
    int a[] = {1, 3, 5, 7, 9, 11, 13, 15};
    int n = sizeof(a) / sizeof(a[0]);
    int x = 7; 
    int l = 0, r = n - 1, m, found = -1;

    while (l &lt;= r) {
        m = (l + r) / 2;
        if (a[m] == x) {
            found = m;
            break;
        } else if (a[m] &lt; x) {
            l = m + 1;
        } else {
            r = m - 1;
        }
    }

    if (found != -1) {
        cout &lt;&lt; "Found at index: " &lt;&lt; found &lt;&lt; endl;
    } else {
        cout &lt;&lt; "Not found" &lt;&lt; endl;
    }

    return 0;
}
</code></pre>

C++<pre><code>#include &lt;iostream&gt;
using namespace std;

int main() {
    int a[] = {1, 3, 5, 7, 9, 11, 13, 15}; // 已排序的数组
    int n = sizeof(a) / sizeof(a[0]); // 数组大小
    int x = 7; // 要查找的目标值
    int l = 0, r = n - 1; // 左边界和右边界初始化
    int m, found = -1; // m为中点索引，found用于标记找到的索引

    while (l &lt;= r) { // 当左边界不超过右边界时继续
        m = (l + r) / 2; // 计算中点
        if (a[m] == x) { // 如果中点值等于目标值
            found = m; // 标记找到的索引
            break; // 退出循环
        } else if (a[m] &lt; x) { // 如果中点值小于目标值
            l = m + 1; // 将左边界右移至中点右侧
        } else { // 如果中点值大于目标值
            r = m - 1; // 将右边界左移至中点左侧
        }
    }

    if (found != -1) { // 判断是否找到
        cout &lt;&lt; "Found at index: " &lt;&lt; found &lt;&lt; endl; // 输出找到的索引
    } else {
        cout &lt;&lt; "Not found" &lt;&lt; endl; // 输出未找到
    }

    return 0;
}
</code></pre>

C++<pre><code>#include&lt;cstdio&gt;
using namespace std;

int n,m,q,a[1000005];

int find(int x) //二分查找 
{
    int l=1,r=n;
    while (l&lt;r)
    {
        int mid=l+(r-l)/2;
        if (a[mid]&gt;=x) r=mid;
        else l=mid+1;
    }

    if (a[l]==x) return l; //找都了就输出他的位置 
    else return -1; // 没找到输出-1 
}

int main()
{
    scanf("%d %d",&amp;n,&amp;m); //读入 

    for (int i=1 ; i&lt;=n ; i++)
        scanf("%d",&amp;a[i]); //还是读入 

    for (int i=1 ; i&lt;=m ; i++)
    {
        scanf("%d",&amp;q);
        int ans=find(q); //看看查找的结果 
        printf("%d ",ans); //输出 
    }

    return 0;
}
</code></pre>

